import React from 'react'
import './Header.css';
import '../style.css';
import Ticket from './Ticket';

export default function openModal() {
  // function openModal() {
  const temp = document.getElementById("modalWindow");
  if (!temp) {
    //модальное окно само
    const backgroundDiv = document.createElement("div");
    backgroundDiv.setAttribute("id", "modalWindow");
    backgroundDiv.setAttribute(
      "className=",
      "position:fixed; top:0; left:0; display: flex; width:100vw; height:100vh; justify-content:center; padding-top: 30px; z-index:1; background-color: rgba(0, 0, 0, 0.7);"
    );
    // backgroundDiv.setAttribute("onclick", "closeModal()");     align-items:center;

    //белый бекграунд
    const modalWinDiv = <div className="modalWinDiv"/>
    //надпись сверху
    const modalTitleDiv = <div className="modalTitleDiv">
    <p>Choose a Tier that's right for you</p>
    </div>

    //дивы с видами билетов
    let modalTicketBasicDiv = <Ticket fontColor="basic-color" name="Basic" 
                                      cost="100$" 
                                      seatSuggest="ticket-suggest-yes" seat="1 Seat Available"
                                      wifiSuggest="ticket-suggest-no"
                                      foodSuggest="ticket-suggest-no" 
                                      />
    let modalTicketStandartDiv = <Ticket fontColor="standart-color" name="Standart" 
                                      cost="150$" 
                                      seatSuggest="ticket-suggest-yes" seat="1 Seat Available"
                                      wifiSuggest="ticket-suggest-yes"
                                      foodSuggest="ticket-suggest-no" 
                                      />
    let modalTicketPremiumDiv = <Ticket fontColor="premium-color" name="Premium" 
                                      cost="200$" 
                                      seatSuggest="ticket-suggest-yes" seat="2 Seat Available"
                                      wifiSuggest="ticket-suggest-yes"
                                      foodSuggest="ticket-suggest-yes" 
                                      />
    
    //див с билетов
    const modalTicketsDiv = <div className="modalTicketsDiv">
                              modalTicketBasicDiv
                              modalTicketStandartDiv
                              modalTicketPremiumDiv
                            </div>

    //див кнопка закрытия
    const modalButton = <button className="button-close" onClick={closeModal}>Закрыть</button>
    const modalButtonDiv = <div className="div-close-button">modalButton</div>

    modalWinDiv.appendChild(modalTicketsDiv);
    modalWinDiv.appendChild(modalButtonDiv);

    backgroundDiv.appendChild(modalWinDiv);

    function closeModal() {
    document.body.removeChild(document.getElementById("modalWindow"));
    }
    
  }
  return (
    <backgroundDiv>

    </backgroundDiv>
  )
}
