import React from 'react'
import './Header.css';
import '../style.css';
import Ticket from './Ticket';

export default function openModal() {
  // function openModal() {
    //надпись сверху
    const modalTitleDiv = <div className="modalTitleDiv">
      <p>Choose a Tier that's right for you</p>
    </div>

    //дивы с видами билетов
    let modalTicketBasicDiv = 
    <Ticket 
      fontColor="basic-color" name="Basic"
      cost="100$"
      seatSuggest="ticket-suggest-yes" seat="1 Seat Available"
      wifiSuggest="ticket-suggest-no"
      foodSuggest="ticket-suggest-no"
    />
    let modalTicketStandartDiv = 
    <Ticket 
      fontColor="standart-color" name="Standart"
      cost="150$"
      seatSuggest="ticket-suggest-yes" seat="1 Seat Available"
      wifiSuggest="ticket-suggest-yes"
      foodSuggest="ticket-suggest-no"
    />
    let modalTicketPremiumDiv = 
    <Ticket 
      fontColor="premium-color" name="Premium"
      cost="200$"
      seatSuggest="ticket-suggest-yes" seat="2 Seat Available"
      wifiSuggest="ticket-suggest-yes"
      foodSuggest="ticket-suggest-yes"
    />

    //див с билетов
    const modalTicketsDiv = <div className="modalTicketsDiv">
      {modalTicketBasicDiv}
      {modalTicketStandartDiv}
      {modalTicketPremiumDiv}
    </div>

    //див кнопка закрытия
    const modalButton = <button className="button-close" onClick={closeModal}>Закрыть</button>
    const modalButtonDiv = <div className="div-close-button">{modalButton}</div>

    //белый бекграунд
    const modalWinDiv = <div className="modalWinDiv">
      {modalTicketsDiv}
      {modalButtonDiv}
    </div>


    //модальное окно само
    const backgroundDiv = <div id="modalWindow" className="backgroundDiv">{modalWinDiv}</div>
    function closeModal() {
      document.body.removeChild(document.getElementById("modalWindow"));
    }
    console.log("ok")
  return (
    <backgroundDiv />
  )
}
