import React from 'react';
import './Carousel.css';

const useHorizontalScrollEvent = (callback) => {
  const positionRef = React.useRef(0);
  return (e) => {
      const x = e.currentTarget.scrollLeft;
      if (x !== positionRef.current) {
          positionRef.current = x;
          callback(e);
      }
  };
};


